package com.tnsif.basicjavaprograms;

public class Code11 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 4;
		int y =8;
		int z=10;
		y += 5; // y = y + 5
		z -= 5; // z = z - 5
		//x *= 5; // x = x * 5
		//x /= 4; // x = x / 4
		
		x %= 5;   // x = x % 5
		System.out.println(x);
		System.out.println(y);
		System.out.println(z);
	}

}
